                        <div className={styles.container2}>
                            {/* <!-- ########### Bild aus auswahl ########## --> */}
                            <div className="fixed-image-panel">
                                <img
                                    alt="Auto Bild"
                                    id="selectedImage"
                                    src={this.state.selectedImage}
                                    onLoad={(e) => {
                                        e.target.style.height = "auto";
                                        e.target.style.width = "100%";
                                    }}
                                />
                            </div>
                            {/* <!-- ########### Bild ENDE ########## --> */}
                        </div>
                    </div>
                </div>
            </main>
        );
    }
}

export default Konfig;
